INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('yycc11',2274,'2020-07-27',5,'This space looks just like the photo. It is clean, homey, private and perfect for a single person who just needs room to sleep (and maybe chill a little bit) in while in NYC. The host Jeff is a great communicator and likely has everything you ll need during your stay.',3);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('integer6474',2749,'2020-01-01',5,'I highly recommend Jeffs place and had a wonderful stay! He is so friendly and ensures you have everything you need for your stay and are comfortable. The place looks just like the photos and is in a great location on the Upper West Side. Its really easy to get around, close to the subway and quiet at night. I felt very safe traveling as a solo female and would definitely stay at Jeffs place again!',8);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('convallis3283',1961,'2019-10-18',4,'Jeff was the most informative and accommodating host! He helped us navigate the best way to get to the Airbnb from the airport and also provided us with helpful tips in getting around the city. He was sweet and his place was awesome.',0);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('semper2147',3869,'2018-03-22',5,'Jeff was such a good host! The place was super unique and tidy and it was in a good spot right by the subway so once we learned the system it was so quick and easy. We would definitely stay with Jeff again the next time were in the city.',9);
