INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('ucc15',2996,'2016-05-01',5,'Jeff is hands down the best airbnb host i have had the privilege of staying with thus far. Not only is the space and location incredible for an extremely fair price, but Jeff will treat you like family. He has excellent local recommendations, a warm heart, and not mention a very friendly canine. I cannot recommend him enough and look forward to staying with him again next time I am in NYC!',9);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('quam10149',3382,'2020-03-19',5,'Jeffs place was one of the best Airbnbs Ive ever stayed in. Super clean and had everything I needed. Also Jeff was a great host, so accommodating and thoughtful. Highly recommend and would definitely stay again.',9);
