INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('Ettu14',846,'2021-08-13',4,'Jeff is an amazing host who makes sure to treat his guests well and provide a comfortable living space for anyone visiting New York. Rick and Buster are a great bonus as well. The location is super convenient for visiting many of the major attractions in Manhattan and is one block behind a great bakery for breakfast/lunch.',0);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('curae84116',4676,'2021-02-02',5,'My first Airbnb experience.  It was fantastic.  Jeff was very accommodating, allowing me to practice my saxophone in the room, making me feel welcome, and providing exactly what was expected, and more.  The best thing I can say is that next time I m in NY, I m going to stay there again.',9);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('ac67183',4252,'2020-09-02',5,'Jeffs place is great for a quick night or week in Manhattan!! Love the private bathroom and ease of ck in and out. Great local suggestions and let me use his nespresso and milk !  Would definitely stay again. Very safe neighborhood and comfy bed. Couldnt figure out how to watch local channels though (wanted to watch the today show this am )',6);
