INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('tristique8035',4446,'2017-08-24',5,'Jeff was great! Easy subway access! Jeff was happy to give tips on things to do, travel, and places to eat! He allowed us a great deal of independence, he was prompt and friendly. Space was clean. Would definitely recommend!',3);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('viverra37167',4786,'2017-11-27',5,'Jeff is very welcoming and communicates well. We coordinated our check-in days in advance. Even though I flew in early, Jeff was accommodating and allowed me to check-in early. The room was great and just as pictured. There were extra blankets available if we needed them and extra towels as well. The room was clean and had everything you would need. The apartment is located near a subway stop so most attractions where with a few minutes away. I would definetly stay here again if I m in New York.',5);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('rhoncus2633',1559,'2020-08-31',5,'My parents in law stayed in Jeff s bedroom for 2 weeks. We picked this location because it was close from where we are living and because the private bathroom (very rare to find when looking for a bedroom to rent).Communication with Jeff was seamless.Check-in and check-out were quick and easy.',8);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('sit21153',1804,'2019-11-18',5,'We stayed at Jeff s place the night before Thanksgiving.  During this busy time Jeff made arrangement for us to drop off our bags early so we could get the most out of our 48 hours there.  The room is private and clean and located in a great location.  Lots of restaurants/diners/bars and close to subway.  Jeff was very kind and helpful.  He was even able to get us passes to Macy s Day Parade... amazing!  His 30 years living in NYC is a huge help to those of us who can only travel there for weekend getaways!  Highly recommend and can t wait to go back.',4);
INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('commodo41119',1210,'2021-07-01',5,'Jeff was friendly, flexible, and made us feel welcome and comfortable. The room had everything we needed and was comfortable. The private bath was nice, as well.',0);
