INSERT INTO accommodations_comments (user_id, accommodation_id, comment_date, rating, comment, comment_likes) VALUES ('pede20152',242,'2019-09-17',4,'Cleber canceled my sister s booking for his second bedroom which threw a spanner in the works for our reunion. He then offered her the couch in my room for an extra $30 a night without consultation with me. Not a great start.  Moving past that, the apartment was clean and well located.',14);
